async function metricMain(){const w=window;if(typeof w.Telegram==="undefined"||typeof w.Telegram.WebApp==="undefined"){return}const telegram=w.Telegram.WebApp;const user=telegram.initDataUnsafe.user;if(typeof user==="undefined"){return}const currentScript=document.currentScript;if(currentScript===null){return}const campaignId=currentScript.getAttribute("campaign_id");if(campaignId===null){return}const data={is_premium:typeof user.is_premium!=="undefined"?user.is_premium:false,init_data:telegram.initData,language:user.language_code,user_id:user.id,url:window.location.href,start_param:telegram.initDataUnsafe.start_param,campaign_id:campaignId};try{await fetch("https://birdton.site/api/nmetrics",{method:"POST",headers:{Accept:"application/json","Content-Type":"application/json"},body:JSON.stringify(data)})}catch(error){}}document.addEventListener("DOMContentLoaded",metricMain);